package io.java;

public class MathUtils {
	
	public int add(int a, int b) {
		return a + b;

	}

	public double computeCircleArea(double radius) {
		// TODO Auto-generated method stub
		return Math.PI*radius*radius;
	}

	public int divide(int i, int j) {
		// TODO Auto-generated method stub
		return i/j;
		
	}

}
